import React,{ useEffect,useState } from "react";
// import { Link } from "react-router-dom";
import axios from 'axios';

function Form() {
    useEffect(() => {
        document.title = 'Form';
    });

    const [user,setUser] = useState({username:'',fileupload:''})

    const insertdata = (e) => {
        var name = e.target.name;
        var value = e.target.value;
        setUser({ ... user, [name]: value});
    }
    const inputfiles = (e) =>{
        var name = e.target.name;
        var value = e.target.files;
        setUser({ ... user, [name]: value});

        console.log(user);
    }

    // const onFileChange = event => {
    //     // Update the state
    //     this.setState({ selectedFile: event.target.files[0] });
    // };

    const submitform = async (e) =>{
        e.preventDefault();

        // var data = new FormData();

        // data.append('name',user.username)
        // data.append('file',user.fileupload)

        const formData = new FormData();

        formData.append("username", user.username);
        formData.append(
            "fileupload",
            user.selectedFile,
            user.selectedFile.name
        );

        axios.post("http://localhost:5000/test",formData).then(res => {
            console.log(res);
        })
        
        // await fetch('http://localhost:5000/test',{
        //     method: "POST",
        //     body: data
        // })
    } 

    return (
        <>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <form method='post' encType="multipart/form-data">
                <input type="text" name="username" onChange={insertdata}/>
                <input type="file" name="fileupload" multiple onChange={inputfiles} />
                <input type="submit" value="save"  onClick={submitform}/>
            </form>
        </>
    )
}

export default Form
