import React,{ useEffect,useState } from "react";
// import { Link } from "react-router-dom";

function Form() {
    useEffect(() => {
        document.title = 'Form';
    });

    const [user,setUser] = useState({
        username:'',
        fileupload:''
    })

    const insertdata = (e) => {
        var name = e.target.name;
        var value = e.target.value;
        console.log(e.target.files)
        // setUser({ ... user, [name]: value});
    }
    const inputfiles = (e) =>{
        var name = e.target.name;
        var value = e.target.value;
    }

    const submitform = async (e) =>{
        e.preventDefault();

        const {username,fileupload} = user;
        
        fetch('http://localhost:5000/test',{
            method: "POST",
            headers:{
                "Content-Type": "application/json"
            },
            body: JSON.stringify({username,fileupload})
        })
    }

    return (
        <>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <form method='post' encType="multipart/form-data">
                <input type="text" name="username" onChange={insertdata}/>
                <input type="file" name="fileupload" onChange={inputfiles}/>
                <input type="submit" value="save" onClick={submitform}/>
            </form>
        </>
    )
}

export default Form
