import React from 'react';
import ReactDOM from 'react-dom';
import {BrowserRouter} from 'react-router-dom'

import jQuery from "jquery";
// import $ from 'jquery';
// import './tesapp.js';

import App from './App';

import Header from './views/header.jsx';

jQuery(function ($) {

  "use strict";

  var win = $(window)
      , target = $('#testing')
      , wrapper = target.find('> div')
      , easing = "ease-out" //css easing
      , duration = "0.8s" //duration ms(millisecond) or s(second)
      , top = 0
      , resizeTimeout
      , jmScroll = {
          _init: function () {
              if (wrapper.length == 1) {
                  target.css({
                      margin: '0',
                      padding: '0',
                      width: '100%',
                      height: wrapper.height() + 'px'
                  });

                  wrapper.css({
                      transition: 'transform ' + duration + ' ' + easing,
                      position: 'fixed',
                      top: '0',
                      left: '0',
                      width: '100%',
                      padding: '0',
                      zIndex: '2',
                      display: 'block',
                      backfaceVisibility: 'hidden'
                  });

                  jmScroll._reFlow(function () {
                      jmScroll._scroll();
                  });
              }
          },

          _scroll: function () {
              top = win.scrollTop();
              wrapper.css('transform', 'translateY(-' + top + 'px)');
          },

          _reFlow: function (callback) {
              clearTimeout(resizeTimeout);
              resizeTimeout = setTimeout(function () {
                  target.height(wrapper.height());

                  var getType = {};
                  var isCallback = callback && getType.toString.call(callback) === '[object Function]';

                  if (isCallback) {
                      callback();
                  }
              }, 200);
          }
      };

  if (typeof window.ontouchstart == 'undefined') {
      win.on({
          scroll: function () {
              jmScroll._scroll();
          }
          , resize: function () {
              jmScroll._reFlow();
          }
          , load: function () {
              jmScroll._init();
          }
      });
  }

  $(window).scroll(function () {

      if (100 < $(window).scrollTop()) {
          jQuery('.laravel_header').css('box-shadow', '0 4px 4px 0px #0000000f')
      }
      else {
          jQuery('.laravel_header').css('box-shadow', 'none')
      }
  })

  jQuery(document).scroll(function (e) {

      var scrolldata = $(document).height() - $(window).height();
      var docudata = $(document).scrollTop()

      jQuery('.laravel_page_scroller').css('width', (docudata * 100) / scrolldata + "%");
  })
});

ReactDOM.render(
  <React.Fragment>
    <BrowserRouter>
      <Header />
      <div id="testing">
        <div classname="container" style={{padding: 0, width: '100%'}}>
          <App />
        </div>
      </div>
    </BrowserRouter>
  </React.Fragment>,
  document.getElementById('root')
);
