import React,{ useEffect,useState } from "react";
// import { Link } from "react-router-dom";
import axios from 'axios';

function Form() {
    useEffect(() => {
        document.title = 'Form';
    });

    const [user,setUser] = useState({username:'',fileupload:''})

    const insertdata = (e) => {
        var name = e.target.name;
        var value = e.target.value;
        setUser({ ... user, [name]: value});
    }
    const inputfiles = (e) =>{
        var name = e.target.name;
        var value = e.target.files[0];
        setUser({ ... user, [name]: value});
    }

    const submitform =  (e) =>{
        e.preventDefault();

        var data = new FormData();

        data.append('name',user.username)
        data.append('file',user.fileupload)
       
        axios.post("http://localhost:5000/test",data).then(res => {
            console.log(res);
        })
        // console.log(data);
        
        // await fetch('http://localhost:5000/test',{
        //     method: "POST",
        //     body: data
        // })
    } 

    const ddd = async () =>{

        const dds = await fetch('http://localhost:5000/api/vacancy',{method: "GET"})
        // const dd = axios.get("http://localhost:5000/api/vacancy");
        console.log(dds);
    }

    ddd();

    return (
        <>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <br/>
            <form method='post' encType="multipart/form-data">
                <select name="vacancy">
                    <option value="v1">v1</option>
                </select>
                <input type="text" name="username" onChange={insertdata}/>
                <input type="file" name="fileupload" multiple onChange={inputfiles} />
                <input type="submit" value="save"  onClick={submitform}/>
            </form>
        </>
    )
}

export default Form









